import React, { useState } from "react";
import NumberFormat from "react-number-format";

const AddButton = (props) => {
  // console.log({ amount, type, description });
  props.setAddForm();
};
function Form(props) {
  const [amount, setAmount] = useState("");
  const [description, setDescription] = useState("");
  const [type, setType] = useState("Income");

  return (
    <div id="transaction-form" className="form-container">
      <div className="form">
        <div style={{ padding: "5px 0px" }} className="form-group">
          <label htmlFor="amount">Amount</label>
        </div>
        <NumberFormat
          className="input"
          value={amount}
          displayType="input"
          type="text"
          thousandSeparator={true}
          thousandsGroupStyle="thousand"
          prefix={"₱ "}
          placeholder="Enter amount here..."
          onChange={(e) => setAmount(e.target.value)}
        />
        <div
          style={{ padding: "5px 0px" }}
          id="for-description"
          className="form-group"
        >
          <label htmlFor="description">Description</label>
          <input
            type="text"
            value={description}
            className="form-control no-bg"
            id="description"
            placeholder="Foods, rent, transportation, etc..."
            onChange={(e) => setDescription(e.target.value)}
            autoComplete="off"
            autoCapitalize="words"
            required
          />
        </div>
        <p>Select the type of transaction:</p>
        <div className="form-check form-check-inline">
          <input
            className="form-check-input"
            type="radio"
            name="transaction"
            id="income"
            value="Income"
            checked={type === "Income"}
            onChange={(e) => setType(e.target.value)}
          />
          <label className="form-check-label" htmlFor="income">
            Income
          </label>
        </div>
        <div className="form-check form-check-inline">
          <input
            className="form-check-input"
            type="radio"
            name="transaction"
            id="expense"
            value="Expense"
            checked={type === "Expense"}
            onChange={(e) => setType(e.target.value)}
          />
          <label className="form-check-label" htmlFor="expense">
            Expense
          </label>
        </div>
        <div className="form-check form-check-inline">
          <input
            className="form-check-input"
            type="radio"
            name="transaction"
            id="saving"
            value="Saving"
            checked={type === "Saving"}
            onChange={(e) => setType(e.target.value)}
          />
          <label className="form-check-label" htmlFor="saving">
            Saving
          </label>
        </div>

        <div className="buttons center">
          <AddButton className="button" onClick={AddButton}>
            Add
          </AddButton>
        </div>
      </div>
    </div>
  );
}

export { Form, AddButton };
