import React, { useState } from "react";
import { Card, CardHeader, CardContent, Grid } from "@material-ui/core";
import { Button } from "@material-ui/core";
import { Link } from "react-router-dom";
import Recent from "../components/Recent/Recent.jsx";
import { BudgetTrackerContext } from "./context/context";
import useStyle from "./pages.js";

const History = (props) => {
  const classes = useStyle();
  const [searchTerm, setSearchTerm] = useState("");
 const { transactions } = useContext(BudgetTrackerContext);
  return (
    <div className={classes.section}>
      <Grid
        container
        alignItems="center"
        justify="center"
        className={classes.container}
        style={{ height: "90vh", backgroundColor: "#EBEBEB" }}
      >
        <Grid item xs={12} sm={6}>
          <Card className={classes.recent}>
            <CardContent>
              <CardHeader title="History" />
              <div className="searchInputs">
                <input
                  type="text"
                  placeholder="Search transactions..."
                  onChange={(e) => {
                    setSearchTerm(e.target.value);
                  }}
                />
                {transactions.filter((value)=>{
                  if(value === (transactions.type || transactions.category)) {
                    return value
                  }
                })}
              </div>

              <div style={{ height: "500", overflow: "auto" }}>
                <Recent />
              </div>
            </CardContent>
            <div className={classes.center}>
              <Link to="/" style={{ textDecoration: "none" }}>
                <Button className={classes.button} variant="contained">
                  Back
                </Button>
              </Link>
            </div>
          </Card>
        </Grid>
      </Grid>
    </div>
  );
};

export default History;
