import React, { useState } from "react";
import Form from "../components/Form";

function Dashboard(props) {
  return (
    <div className="section">
      <h1>Dashboard</h1>
      <div className="dashboard-container">
        <div className="balance">
          <h5>Your Balance</h5>
          <p id="balance-money">₱0.00</p>
        </div>
      </div>
      <div className="savings-expenses center">
        <div className="savings">
          <h5>Savings</h5>
          <p id="saving-money">₱0.00</p>
        </div>
        <div className="expenses">
          <h5>Expenses</h5>
          <p id="expense-money">₱0.00</p>
        </div>
        <div id="add-transaction" className="buttons center">
          <addButton
            className="button"
            onClick={() => setAddForm((isVisible) => !isVisible)}
          >
            {addForm ? "Cancel" : "Add"}
          </addButton>
        </div>
      </div>
      <div id="form">
        {addForm && (
          <Form setAddForm={setAddForm} addRecent={props.addRecent} />
        )}
      </div>
      {/* <RecentTransaction recents={recents}></RecentTransaction> */}
    </div>
  );
}

export default Dashboard;
