import React, { useContext, useState } from "react";
import {
  Card,
  CardHeader,
  CardContent,
  Grid,
  TextField,
  InputAdornment,
  IconButton,
  Button,
} from "@material-ui/core";
import { Link } from "react-router-dom";
import Recent from "../components/Recent/Recent.jsx";
import useStyle from "./pages.js";
import { BudgetTrackerContext } from "../context/context";

import SearchRoundedIcon from "@material-ui/icons/SearchRounded";
import ClearRoundedIcon from "@material-ui/icons/ClearRounded";

const History = (props) => {
  const classes = useStyle();
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const { transactions } = useContext(BudgetTrackerContext);

  const getSearchTerm = (e) => {
    setSearchTerm(e.target.value);
  };

  const searchHandle = (searchTerm) => {
    setSearchTerm(searchTerm);
    if (searchTerm !== "") {
      const filteredTransactions = transactions.filter((transaction) => {
        console.log(transactions.type);
        return transactions.value
          .join(" ")
          .toLowerCase()
          .includes(searchTerm.toLowerCase());
      });
      setSearchResults(filteredTransactions);
      // } else {
      setSearchResults(transactions);
    }
  };

  return (
    <div className={classes.section}>
      <Grid
        container
        alignItems="center"
        justifyContent="center"
        className={classes.container}
        style={{ height: "90vh", backgroundColor: "#EBEBEB" }}
      >
        <Grid item xs={12} sm={6}>
          <Card className={classes.recent}>
            <CardContent>
              <CardHeader title="History" />
              <div className={classes.search}>
                <TextField
                  variant="outlined"
                  size="small"
                  type="text"
                  placeholder="Search transaction..."
                  onChange={getSearchTerm}
                  value={props.term}
                  className={classes.searchBar}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <SearchRoundedIcon />
                      </InputAdornment>
                    ),
                    endAdornment: getSearchTerm && (
                      <IconButton
                        aria-label="clear icon"
                        onClick={() => (TextField.value = "")}
                      >
                        <ClearRoundedIcon />
                      </IconButton>
                    ),
                  }}
                />
              </div>
              <div style={{ height: "480px", overflow: "auto" }}>
                <Recent
                  recent={searchTerm.length < 1 ? transactions : searchResults}
                  term={searchTerm}
                  searchKeyword={searchHandle}
                />
              </div>
            </CardContent>
            <div className={classes.center}>
              <Link to="/" style={{ textDecoration: "none" }}>
                <Button className={classes.button} variant="contained">
                  Dashboard
                </Button>
              </Link>
            </div>
          </Card>
        </Grid>
      </Grid>
    </div>
  );
};

export default History;
