import { makeStyles } from "@material-ui/core/styles";

export default makeStyles(() => ({
  income: {
    padding: "20px",
    borderRadius: "10px",
    // backgroundColor: "#D3E4CD",
    boxShadow: "0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23)",
  },
  expense: {
    padding: "20px 0",
    borderRadius: "10px",
    // backgroundColor: "#EEC4C4",
    boxShadow: "0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23)",
  },
  saving: {
    borderRadius: "10px",
    // backgroundColor: "#A7C5EB",
    boxShadow: "0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23)",
  },
  overview: {
    borderRadius: "10px",
    boxShadow: "0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23)",
  },
  chartContainer: {
    height: "400px",
  },
}));
