import React, { useContext, useRef, useState } from "react";
import {
  Card,
  CardHeader,
  CardContent,
  Grid,
  TextField,
  InputAdornment,
  IconButton,
  Button,
} from "@material-ui/core";
import SearchRoundedIcon from "@material-ui/icons/SearchRounded";
import ClearRoundedIcon from "@material-ui/icons/ClearRounded";
import { Link } from "react-router-dom";
import Recent from "../components/Recent/Recent.jsx";
import { BudgetTrackerContext } from "../context/context.js";
import useStyle from "./pages.js";

const History = (props) => {
  const classes = useStyle();
  const { transactions } = useContext(BudgetTrackerContext);
  const [searchTerm, setSearchTerm] = useState("");

  return (
    <div className={classes.section}>
      <Grid
        container
        alignItems="center"
        justify="center"
        className={classes.container}
        style={{ height: "90vh", backgroundColor: "#EBEBEB" }}
      >
        <Grid item xs={12} sm={6}>
          <Card className={classes.recent}>
            <CardContent>
              <CardHeader title="History" />
              <div className={classes.search}>
                <TextField
                  variant="outlined"
                  size="small"
                  type="text"
                  placeholder="Search transaction..."
                  onChange={(e) => setValue(e.target.value)}
                  value={value}
                  className={classes.searchBar}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <SearchRoundedIcon />
                      </InputAdornment>
                    ),
                    endAdornment: searchTerm && (
                      <IconButton
                        aria-label="clear icon"
                        onClick={() => setSearchTerm("")}
                      >
                        <ClearRoundedIcon />
                      </IconButton>
                    ),
                  }}
                />
              </div>

              <div style={{ height: "480px", overflow: "auto" }}>
                <Recent />
              </div>
            </CardContent>
            <div className={classes.center}>
              <Link to="/" style={{ textDecoration: "none" }}>
                <Button className={classes.button} variant="contained">
                  Back
                </Button>
              </Link>
            </div>
          </Card>
        </Grid>
      </Grid>
    </div>
  );
};

export default History;
