import React, { useState } from "react";

const Description = () => {
  const [description, setDescription] = useState();
  return (
    <div
      style={{ padding: "5px 0px" }}
      id="for-description"
      className="form-group"
    >
      <label htmlFor="description">Description (optional)</label>
      <input
        type="text"
        value={description}
        className="form-control no-bg"
        id="description"
        placeholder="Enter text here..."
        onChange={(e) => setDescription(e.target.value)}
      />
    </div>
  );
};
const Other = () => {
  const [description, setDescription] = useState();
  return (
    <div className="form-group">
      <label htmlFor="other">Other</label>
      <input
        type="text"
        className="form-control no-bg"
        id="other"
        value={description}
        placeholder="Enter text here..."
        onChange={(e) => setDescription(e.target.value)}
        required
        autoFocus
      />
    </div>
  );
};
const ForExpense = (props) => {
  const [description, setDescription] = useState();

  return (
    <div id="for-expense">
      <div style={{ padding: "5px 0px" }}>
        <select
          id="other-option"
          className="form-select form-select-sm"
          aria-label=".form-select-sm"
          value={select}
          onChange={(e) => setDescription(e.target.value)}
          required
        >
          <option value="default" disabled hidden>
            What type of expense?
          </option>
          <option value="bills">Bills</option>
          <option value="entertainment">Entertainment</option>
          <option value="food">Food</option>
          <option value="health">Health</option>
          <option value="payment">Payment</option>
          <option value="rent">Rent</option>
          <option value="school">School</option>
          <option value="transportation">Transportation</option>
          <option value="utilities">Utilities</option>
          <option value="other">Other</option>
        </select>
      </div>
      <div>{select === "other" && <Other />}</div>
    </div>
  );
};
function Form(props) {
  const [isSelected, setIsSelected] = useState(false);
  const [amount, setAmount] = useState();
  const [type, setType] = useState("Income");
  const handleRadio = (status) => {
    setIsSelected(status);
  };
  return (
    <div id="transaction-form" className="form-container">
      <form action="/" method="post" autoComplete="off">
        <div style={{ padding: "5px 0px" }} className="form-group">
          <label htmlFor="amount">Amount</label>
          <input
            type="number"
            value={amount}
            className="form-control no-bg padding"
            id="amount"
            placeholder="Enter amount here..."
            onChange={(e) => setAmount(e.target.value)}
          />
        </div>
        <p>Select the type of transaction:</p>
        <div className="form-check form-check-inline">
          <input
            className="form-check-input"
            type="radio"
            name="transaction"
            id="income"
            value="income"
            onChange={(e) => setType(e.target.value)}
            onClick={(e) => handleRadio(false)}
            defaultChecked
          />
          <label className="form-check-label" htmlFor="income">
            Income
          </label>
        </div>
        <div className="form-check form-check-inline">
          <input
            className="form-check-input"
            type="radio"
            name="transaction"
            id="expense"
            value="expense"
            onChange={(e) => setType(e.target.value)}
            onClick={(e) => handleRadio(true)}
          />
          <label className="form-check-label" htmlFor="expense">
            Expense
          </label>
        </div>
        <div className="form-check form-check-inline">
          <input
            className="form-check-input"
            type="radio"
            name="transaction"
            id="saving"
            value="saving"
            onChange={(e) => setType(e.target.value)}
            onClick={(e) => handleRadio(false)}
          />
          <label className="form-check-label" htmlFor="saving">
            Saving
          </label>
        </div>
        {isSelected === true ? <ForExpense /> : <Description />}
        <div className="buttons center">
          <button type="submit" id="add-btn" className="btn">
            Add
          </button>
        </div>
      </form>
    </div>
  );
}
export default Form;
