import React, { useContext } from "react";
import { Card, CardHeader, CardContent } from "@material-ui/core";
import {
  List as MUIList,
  ListItem,
  ListItemAvatar,
  Avatar,
  ListItemText,
  ListItemSecondaryAction,
  Button,
  IconButton,
  Slide,
} from "@material-ui/core";
import { Link } from "react-router-dom";
import formatAmount from "../../utility/formatAmount";
import { ClearRounded, MoneyOffRounded } from "@material-ui/icons";
import AttachMoneyRoundedIcon from "@material-ui/icons/AttachMoneyRounded";
import AccountBalanceRoundedIcon from "@material-ui/icons/AccountBalanceRounded";

import { BudgetTrackerContext } from "../../context/context";
import useStyles from "./RecentStyle.js";

const List = () => {
  const classes = useStyles();
  const { transactions, deleteTransaction } = useContext(BudgetTrackerContext);
  // const transactions = [
  //   {
  //     id: 1,
  //     type: "Income",
  //     category: "Salary",
  //     amount: "50000",
  //     date: "Thur Dec 2",
  //   },
  //   {
  //     id: 2,
  //     type: "Saving",
  //     category: "For house",
  //     amount: "10000",
  //     date: "Fri Dec 3",
  //   },
  //   {
  //     id: 3,
  //     type: "Expense",
  //     category: "Groceries",
  //     amount: "2000",
  //     date: "Fri Dec 3",
  //   },
  // ];

  return (
    <Card className={classes.recent}>
      <CardContent>
        <CardHeader title="Recent transactions" />
        <MUIList dense={false} className={classes.list}>
          {transactions.map((transaction) => (
            <Slide
              direction="down"
              in
              mountOnEnter
              unmountOnExit
              key={transaction.id}
            >
              <ListItem>
                <ListItemAvatar>
                  <Avatar
                    className={
                      transaction.type === "Income"
                        ? classes.avatarIncome
                        : transaction.type === "Expense"
                        ? classes.avatarExpense
                        : classes.avatarSaving
                    }
                  >
                    {transaction.type === "Expense" ? (
                      <MoneyOffRounded />
                    ) : transaction.type === "Income" ? (
                      <AttachMoneyRoundedIcon />
                    ) : (
                      <AccountBalanceRoundedIcon />
                    )}
                  </Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={transaction.category}
                  secondary={`₱${formatAmount(transaction.amount)} - ${
                    transaction.date
                  }`}
                />
                <ListItemSecondaryAction>
                  <IconButton
                    edge="end"
                    aria-label="delete"
                    onClick={() => deleteTransaction(transaction.id)}
                  >
                    <ClearRounded />
                  </IconButton>
                </ListItemSecondaryAction>
              </ListItem>
            </Slide>
          ))}
        </MUIList>
      </CardContent>
      <div className={classes.center}>
        <Link to="/history" style={{ textDecoration: "none" }}>
          <Button className={classes.button} variant="contained">
            View all
          </Button>
        </Link>
      </div>
    </Card>
  );
};

export default List;
