import React from "react";
import Form from "../components/Form";

function Dashboard() {
  const form = document.getElementById("form");
  function handleClick() {
    if ((form.style.display = "none")) {
      form.style.display = "block";
    } else {
      form.style.display = "none";
    }
  }
  return (
    <div className="section">
      <h1>Dashboard</h1>
      <div className="dashboard-container">
        <div className="balance">
          <h5>Your Balance</h5>
          <p id="balance-money">$0.00</p>
        </div>
      </div>
      <div className="savings-expenses center">
        <div className="savings">
          <h5>Savings</h5>
          <p id="saving-money">$0.00</p>
        </div>
        <div className="expenses">
          <h5>Expenses</h5>
          <p id="expense-money">$0.00</p>
        </div>
        <div id="add-transaction" className="center">
          <button id="add-btn" onClick={handleClick}>
            Add
          </button>
          <button id="cancel-btn" onClick={handleClick}>
            Cancel
          </button>
        </div>
      </div>
      <div id="form" style={{ display: "none" }}>
        <Form />
      </div>
    </div>
  );
}

export default Dashboard;
