import React, { useState } from "react";
import {
  Card,
  CardHeader,
  CardContent,
  Typography,
  Grid,
  Button,
} from "@material-ui/core";
// import ExpandMoreRoundedIcon from "@material-ui/icons/ExpandMoreRounded";
import AddCircleRoundedIcon from "@material-ui/icons/AddCircleRounded";
import Form from "./Form/Form.jsx";

import useStyles from "./TransactionStyle.js";

const Transaction = () => {
  const [expand, setExpand] = useState(false);
  const classes = useStyles();
  const handleExpand = () => {
    setExpand(true);
  };
  return (
    <Card className={classes.overview}>
      <CardContent className={classes.overviewContent}>
        <CardHeader title="Dashboard" />
        <Typography align="left" variant="h6">
          Your Balance: ₱ 0.00
        </Typography>
        <CardContent>
          <Grid container spacing={1}>
            <Grid item xs={6}>
              <Typography align="center" variant="p">
                Saving
              </Typography>
            </Grid>
            <Grid item xs={6}>
              <Typography align="center" variant="p">
                Expense
              </Typography>
            </Grid>
          </Grid>
          <Grid container spacing={1}>
            <Grid item xs={6}>
              <Typography align="center" variant="p">
                ₱ 0.00
              </Typography>
            </Grid>
            <Grid item xs={6}>
              <Typography align="center" variant="p">
                ₱ 0.00
              </Typography>
            </Grid>
          </Grid>
        </CardContent>
        <Button onClick={handleExpand}>
          {/* <ExpandMoreRoundedIcon fontSize="large" style={{ color: "white" }} /> */}
          <AddCircleRoundedIcon fontSize="large" style={{ color: "white" }} />
        </Button>
      </CardContent>
      <CardContent className={classes.formContent}>
        <Form />
      </CardContent>
    </Card>
  );
};

export default Transaction;
