import React from "react";
import { Card, CardHeader, CardContent, Grid } from "@material-ui/core";
import { Button } from "@material-ui/core";
import { Link } from "react-router-dom";
import Recent from "../components/Recent/Recent.jsx";
import SearchBar from "../utility/Search/Search.jsx";
import useTransactions from "../useTransactions";
import useStyle from "./pages.js";

const History = (props) => {
  const classes = useStyle();
  const { chartData } = useTransactions(title);
  return (
    <div className={classes.section}>
      <Grid
        container
        alignItems="center"
        justify="center"
        className={classes.container}
        style={{ height: "90vh", backgroundColor: "#EBEBEB" }}
      >
        <Grid item xs={12} sm={6}>
          <Card className={classes.recent}>
            <CardContent>
              <CardHeader title="History" />
              <div>
                <SearchBar
                  placeholder="Search transactions..."
                  data={chartData}
                />
              </div>
              <div style={{ height: "500", overflow: "auto" }}>
                <Recent />
              </div>
            </CardContent>
            <div className={classes.center}>
              <Link to="/" style={{ textDecoration: "none" }}>
                <Button className={classes.button} variant="contained">
                  Back
                </Button>
              </Link>
            </div>
          </Card>
        </Grid>
      </Grid>
    </div>
  );
};

export default History;
